mapper.insert:
INSERT INTO bbs(title, username, date, time, content, camp_id
) VALUES (
#{title}, #{user.username}, #{date}, #{time}, #{content}, #{campId}
)

INSERT INTO image(bbs_id,
img, original_img, username
) VALUES (
#{bbs.bbsId}, #{img},
#{original_img}, #{user.username}
)

INSERT INTO video(bbs_id,
data, username
) VALUES (
#{bbs.bbsId}, #{data}, #{user.username}
)

mapper.update:
UPDATE bbs
SET
title = #{title},
content = #{content}
WHERE bbs_id = #{bbsId}
